import axios from 'axios'
const rootUrl ="http://localhost:8000/api/v1/task"
export const fetchTasks = async ()=>{
    try {
        return axios.get(rootUrl);

    }catch(error){
        console.log(error)
        return{
            status:"error",
            message:"error,Monitor error"
        }
    }
    }
    export const addTask = async(taskObj)=>{
        try {
            return axios.post(rootUrl,taskObj);

        }catch(error){
            console.log(error)
            return{
                status:"error",
                message:"error,Monitor error"
            }
        }

    }
    export const deleteTasks= async(_idsArg)=>{
        try {
            return axios.deleteTasks(rootUrl,{dat:_idsArg});

        }catch(error){
            console.log(error)
            return{
                status:"error",
                message:"error,Monitor error"
            }
        }

    }
